mapper.select:
SELECT COUNT(*) AS checkValue
FROM lpguser
WHERE userId = #{userId}

SELECT COUNT(*) AS checkValue
FROM lpguser
WHERE userId = #{userId}
AND userEmail=#{userEmail}

mapper.update:
update lpguser
set userPwd=#{userPwd}
where userId = #{userId}

mapper.select:
SELECT COUNT(*) AS checkValue
FROM lpguser
WHERE userId = #{userId}
AND userPwd=#{userPwd}
